    public static ArrayList<Drakor> getListData() {
        Drakor drakor;
        ArrayList<Drakor> list = new ArrayList<>();

        for (String[] mData : data) {
            drakor = new Drakor();
            drakor.setNama(mData[0]);
            drakor.setGenre(mData[1]);
            drakor.setFoto(mData[2]);
            drakor.setDeskripsi(mData[3]);
            drakor.setPemeran(mData[4]);

            list.add(drakor);
        }

        return list;
    }
